import React, {useEffect, useState} from 'react';
import Wrapper from "./Wrapper";
import {Product} from "../../interfaces/Product";
import {Link} from 'react-router-dom';


const Products = () => {
    const [products, setProduct] = useState([])

    useEffect(() => {
        (async () => {
            const response = await fetch('http://localhost:8000/api/products');
            const data = await response.json();
            setProduct(data);
        })();
    }, []);
    const del = async (id: number) => {
        if (window.confirm('Are you sure you want to delete this product?')) {
            await fetch(`http://localhost:8000/api/products/${id}`, {
                method: 'DELETE'
            });
            setProduct(products.filter((p: Product) => p.id !== id));
        }

    }
    return (
        <Wrapper>
            <div className="pt-3 pb-2 mb-3 border-bottom">
                <div className="btn-toolbar mb-2 mb-md-0">
                    {/* eslint-disable-next-line react/jsx-no-undef */}
                    <Link to='/admin/products/create' className="btn btn-sm btn-outline-secondary">Add</Link>
                </div>
            </div>

            <div>
                <div className="table-responsive">
                    <table className="table table-striped table-sm">
                        <thead>
                        <tr>
                            <th>#</th>
                            <th>Image</th>
                            <th>Title</th>
                            <th>Likes</th>
                            <th>Action</th>
                        </tr>
                        </thead>
                        <tbody>
                        {products.map(
                            (p: Product) => {
                                return (
                                    <tr key={p.id}>
                                        <td>{p.id}</td>
                                        <td><img src={p.image} height="180"/></td>
                                        <td>{p.title}</td>
                                        <td>{p.likes}</td>
                                        <td>
                                            <div className="btn-group mr-2">
                                                <Link to={`/admin/products/${p.id}/edit`}  href="#" className="btn btn-sm btn-outline-secondary">Edit</Link>
                                                <a href="#" className="btn btn-sm btn-outline-secondary"
                                                   onClick={() => del(p.id)}
                                                >Delete</a>
                                            </div>
                                        </td>
                                    </tr>
                                )
                            })}


                        </tbody>
                    </table>
                </div>
            </div>
        </Wrapper>
    );
};

export default Products;
